package assignment_1_basic_program;

public class Program_4_Inline_Computation {
	/*
	 Write a Java program to print the result of the following operations.
		Test Data:
		a. -5 + 8 * 6
		b. (55+9) % 9
		c. 20 + -3*5 / 8
		d. 5 + 15 / 3 * 2 - 8 % 3
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.print("First Expression: -5 + 8 * 6 = ");
		System.out.println(-5+8*6);
		System.out.print("Second Expression: (55+9) % 9 = ");
		System.out.println((55+9)%9);
		System.out.print("Third Expression: 20 + -3*5 / 8 = ");
		System.out.println(20+(-3*5)/8);
		System.out.print("Fourth Expression: 5 + 15 / 3 * 2 - 8 % 3 = ");
		System.out.println(5+15/3*2-8%3);
	}

}
